function q0 = fcn()
p = 6657000;   % m
mu = 3.986e14; % m^3/s^2
w = sqrt(mu/p^3);

ri = (p/sqrt(2))*[-cos(0); sqrt(2)*sin(0); cos(0)];
bx = -ri/norm(ri);

ridot = (w*p/sqrt(2))*[sin(0); sqrt(2)*cos(0); -sin(0)];
by = ridot/norm(ridot);

bz = cross(bx, by);

DCM = [bx'; by'; bz'];

qs = .5*sqrt(1 + trace(DCM));
qx = (DCM(3,2) - DCM(2,3))/(4*qs);
qy = (DCM(1,3) - DCM(3,1))/(4*qs);
qz = (DCM(2,1) - DCM(1,2))/(4*qs);

q0nom = [qx; qy; qz; qs];

thetaerr = 2;
qerr = [0; sind(thetaerr/2); 0; cosd(thetaerr/2)];

q0 = qmult(qerr, q0nom);

function [ri, vel, qnom, DCM] = Pos_Vel(t)

p = 6657000;   % m
mu = 3.986e14; % m^3/s^2
w = sqrt(mu/p^3);

ri = (p/sqrt(2))*[-cos(w*t); sqrt(2)*sin(w*t); cos(w*t)];
bx = -ri/norm(ri);

ridot = (w*p/sqrt(2))*[sin(w*t); sqrt(2)*cos(w*t); -sin(w*t)];
by = ridot/norm(ridot);

bz = cross(bx, by);

DCM = [bx'; by'; bz'];

qs = .5*sqrt(1 + trace(DCM))*sign(DCM(2,1));
qx = (DCM(2,3) - DCM(3,2))/(4*qs);
qy = (DCM(3,1) - DCM(1,3))/(4*qs);
qz = (DCM(1,2) - DCM(2,1))/(4*qs);

qnom = [qx; qy; qz; qs]; % inertial to body quaternion - scalar part last

vel = ridot;

function DCMest = TRIAD(d1b, magmeas, magact, pos)

d1i = -pos/norm(pos);
d2i = magact/norm(magact);

d2b = magmeas/norm(magmeas);


xb = d1b;
yb = cross(d1b, d2b)/norm(cross(d1b, d2b));
zb = cross(xb, yb);

xi = d1i;
yi = cross(d1i, d2i)/norm(cross(d1i, d2i));
zi = cross(xi, yi);

DCMest = [xb yb zb]*[xi yi zi]';

function wmeas = Measure_Ang_Vel(meas_flag, wact, time)

b = 1;             % gyro bias deg/hr

b = b*pi/180/3600; % gyro bias rad/s

gyro_walk = .22;     % angular random walk vgyro(t)
                      % deg/hr

vgyro = (gyro_walk*(pi/180)/(60))^2; % rad/s

gyro_walk_randomize = [.8147; .9058; .1270]; 
% constants from MATLAB rand to ensure non-uniform walk in 
% each direction
perturbations = b + vgyro*time*gyro_walk_randomize;

if ~meas_flag
    perturbations = [0; 0; 0];
end

wmeas = wact + perturbations;

function Bmeas = mag_measure(err_flag, Tib, Bi, randerr)

vmag = 0;

if err_flag
    vmag = 8e-9;  % magnetomer error vmag(t)
end


Bmeas = Tib*Bi + vmag*randerr;

function Tib = DCM(qact)

qv = qact(1:3);

qvx = [0 -qv(3) qv(2);
    qv(3) 0 -qv(1);
    -qv(2) qv(1) 0];

Tib = eye(3) - 2*qact(4)*qvx + 2*qvx*qvx;

function Bi = Magnetic_Field(ri)

Bo = 3.12e-5;
Re = 6378000;

nz2i = -ri/norm(ri);
ny2i = cross(nz2i, [1; 0; 0])/norm(cross(nz2i, [1; 0; 0]));
nx2i = cross(ny2i, nz2i);
Ti2n = [nx2i'; ny2i'; nz2i'];

rxy = sqrt(ri(1)^2 + ri(2)^2);

lat = atan2(ri(3), rxy);

Bn = Bo*(Re/norm(ri))^3*[cos(lat); 0; -2*sin(lat)];

Bi = (Ti2n')*Bn;

function dmeas = Horizon_Sensor(Tib, randerr, err_flag, ri)

horizon_sensor_err = 1; %deg
scale = horizon_sensor_err*pi/180;

if ~err_flag
    scale = 0;
end

Trand = eye(3) - scale*randerr;  % horizon error vhor -> random 3x3 err mat

dmeas = Trand*Tib*(-ri/norm(ri));

function DCMnom = Nominal_Attitude(ri, ridot)

bx = -ri/norm(ri);

by = ridot/norm(ridot);

bz = cross(bx, by);

DCMnom = [bx'; by'; bz'];

function err = Error(dAtt)

err = -.5*[dAtt(3,2) - dAtt(2,3);
    dAtt(1,3) - dAtt(3,1);
    dAtt(2,1) - dAtt(1,2)];

function edot = errdot(wnom, dw, ep)

wx = wnom(1); wy = wnom(2); wz = wnom(3);
wnomcross = [0 -wz wy;
    wz 0 -wx;
    -wy wx 0];

edot = -wnomcross*ep + dw;

function RxnRateAct = Rxn_Rates_Actual(RxnRates, randerr, err_flag)

Rpm_Max = 10000;
reaction_wheel_error = 5; % +- reaction wheel rpm error
w_Percent_Err = reaction_wheel_error/Rpm_Max;
pert = w_Percent_Err*randerr*norm(RxnRates);

if ~err_flag
    pert = [0; 0; 0];
end

RxnRateAct = RxnRates + pert;

function [dw_sc, dq] = ...
ang_accel(w_sc, J, Hdotwheel, Hwheel, q, vel, DCM, pos)

% === Aerodynamic TOrque Perturbation === %
A = .1*.1; % m^2
Cd = 4;
p = 2e-11; %kg/m^3
vhat = vel/norm(vel);
Fd = -.5*p*dot(vel, vel)*Cd*A*DCM*vhat*dot(vhat, [0 1 0]);

r_cg = [.01; .15; .01]; % 3u cubesat

Maero = cross(r_cg, Fd);

% === Gravity Gradient Perturbation === %

GM = 3.986e14;
r = norm(pos);
Mgrav = 3*GM*cross(pos, J*pos)/(r^5);



dw_sc = J\(cross(w_sc, J*w_sc) - Hdotwheel ...
 - cross(w_sc, Hwheel) + Maero + Mgrav);

wq = [w_sc(1); w_sc(2); w_sc(3); 0];

dq = .5*qmult(wq, q);
